import React, { Fragment,useState } from 'react'
import './mainMenu.css'
import Grains from './grains/Grains'
import RealEstate from './realEstate/RealEstate';

const MainMenu = (props) => {
    const [currentScreen, setCurrentScreen] = useState('mainMenu');

    const changeToTestPage = () => {
        setCurrentScreen('grains');
    }

    const changeToIntroPage = () => {
        setCurrentScreen('realEstate');
    }

    let renderedScreen;
    switch (currentScreen) {
        case 'grains':
            renderedScreen = <Grains changeToIntroPage={changeToIntroPage} />;
            break;
        default:
            renderedScreen = <RealEstate onChangeToTestPage={changeToTestPage} />;

    }
    return (
        <Fragment>
            <div className='report'>
            {/* todo: Figure out x, y, z */}
                <h1>Big Alien Mafia Gangster Boss</h1>
                <p>Human Beings ate x% of your food!</p>
                <p>y Weather z Harvest</p>
            </div>
            <div className='header'>
            {/* todo: numbers must be defined */}
                <div className='grains'>
                    Grains Reserve number Bushels
                </div>
                <div className='grain-demand'>
                    Grains Demand numberBushels
                </div>
                <div className= 'grain-price'>Price of Grain Number1 Number2 St</div>
                <div className='land-price'>Price Of Land number Acre</div>
                <div className='treasury-gold'>Treasury Gold Number Florins</div>
            </div>
            <p> Press Zero To continue Or Number Of Transactions If You Wish To Buy Or Sell</p>
            <div className='option-1'>
                <div className='row-1'>
                    <button className='Start-btn' onClick={props.onChangeToGrains}>Grain Market</button>
                </div>
            </div> 
            <div className='option-2'>
                <div className='row-2'>
                    <button className='Start-btn' onClick={props.onChangeToRealEstate}>Land Office</button>
                </div>
            </div> 
            
                
        <div className="MainMenuScreen">
            {renderedScreen}
        </div>
    </Fragment>
    )
    
}

export default MainMenu
